mapper.select:
select distinct gugunName 
from baseaddress 
where sidoName = #{sidoName}

select distinct dongName, dongCode, lat, lng
from baseaddress 
where sidoName = #{sidoName} and gugunName = #{gugunName}

select dongcode 
from dongcode 
where dongname=#{dongName} and sidoName = #{sidoName}

		select hi.apartmentName, hd.floor, hi.dong as 'dongName', hd.dealYear, hd.dealMonth, hd.area, hd.dealamount, hi.lat, hi.lng, hd.aptcode
         from housedeal hd, houseinfo hi
         where hd.aptCode = hi.aptcode and hi.dongcode = #{dongCode}
         and dealyear = #{dealYear} and dealmonth = #{dealMonth} 
         limit #{startRow}, #{count}

		select count(hi.apartmentName)
        from housedeal hd, houseinfo hi
        where hd.aptCode = hi.aptcode and hi.dongcode = #{dongCode}
        and dealyear = #{dealYear} and dealmonth = #{dealMonth}

 	select hi.apartmentName, hi.roadName, hi.jibun, hi.dong, hi.buildYear, hd.dealAmount, hd.area, hd.floor, hd.dealYear, hd.dealMonth, hi.lat, hi.lng, hi.dong as 'dongName', hd.aptcode
	from housedeal hd, houseinfo hi
	where hd.aptCode = hi.aptcode and hi.apartmentName=#{apartmentName} and hd.floor=#{floor}
	and dealyear = #{dealYear} and dealmonth = #{dealMonth};
