mapper.select:

SELECT BNO, TITLE, content, user_id, regtime, hit, sido, gugun 
FROM QNA_BOARD
WHERE gugun = #{gugun}
ORDER BY BNO DESC
LIMIT #{start}, #{cnt}

SELECT COUNT(*)
FROM QNA_BOARD
WHERE gugun = #{gugun}

