mapper.select:
select user_name, user_id, phone
from member
where user_id = #{userId} and user_pw = #{userPw}

select user_name, user_id
from member
where user_id = #{userid}

select token
from member
where user_id = #{userid}

mapper.update:
update member
set token = #{token}
where user_id = #{userid}

update member
set token = #{token, jdbcType=VARCHAR}
where user_id = #{userid}
